mapper.select:
SELECT 
	w.WAYBILL_NO
FROM 
	pisp_waybill w 
WHERE 
	w.INFO_TYPE = 1 
AND w.INFO_ID = #{infoId} 	
AND w.HAULIER = #{userId} 	
AND w.STATUS = #{status}

SELECT pwb.*, pwg.PRICE, pricedict.VAL_NAME as PRICE_UNIT, ppe.USER_TYPE, ppe.REAL_NAME, ppe.NICKNAME, ppe.MOBILE_PHONE FROM pisp_waybill pwb LEFT JOIN pisp_waybill_goods pwg on pwg.WAYBILL_NO = pwb.WAYBILL_NO LEFT JOIN pisp_v_user_puser_euser ppe ON pwb.USER_ID = ppe.USER_ID LEFT JOIN pisp_dict_val pricedict ON pricedict.DICT_CODE = 'TRANSPORT_PRICE_UNIT' AND pricedict.VAL_CODE = pwg.PRICE_UNIT
and pwb.INFO_ID = #{infoId}
and pwb.INFO_TYPE = #{infoType}

SELECT COUNT(0) from pisp_waybill w
INNER JOIN pisp_publish_info pi ON pi.INFO_ID = w.INFO_ID AND pi.INFO_TYPE = w.INFO_TYPE
INNER JOIN pisp_car_source cs ON w.INFO_ID = cs.CAR_SOURCE_ID

	 AND w.INFO_TYPE = #{infoType}
	 AND cs.SOURCE_TYPE = #{sourceType}
	 AND pi.USER_ID = #{userId}
	 AND w.STATUS = #{status}

SELECT COUNT(0) from pisp_waybill w


		AND (w.SHIPPER = #{userId} OR w.HAULIER = #{userId} )


		AND w.SHIPPER = #{shipper}


		AND w.HAULIER = #{haulier}


		AND w.STATUS = #{status}


		AND date_format(w.TRANSACTION_TIME,'%Y-%m-%d') = #{currentDate}


		AND w.USER_ID = #{createUserId}


		AND date_format(w.CREATE_TIME,'%Y-%m-%d') = #{createDate}


		 AND w.PROCESS_TYPE = #{processType}
		 AND w.PROCESS_TYPE != #{processType}

SELECT w.PROCESS_TYPE 
FROM pisp_waybill w 
WHERE w.WAYBILL_NO = #{waybillNo}

SELECT * FROM ( SELECT pi.PUBLISH_ID, pi.INFO_ID, gs.FROM_PLACE, gs.TO_PLACE, gs.INFO_CONTENT, gs.GOODS_CLASS, pi.PUBLISH_TIME, ( SELECT COUNT(w.WAYBILL_NO) FROM pisp_waybill w WHERE w.INFO_ID = pi.INFO_ID AND w.INFO_TYPE = pi.INFO_TYPE AND w. STATUS != 99 AND w. ORDER_STATUS is NULL AND w.PROCESS_TYPE = 1 ) AS TOTAL_NUM FROM pisp_publish_info pi LEFT JOIN pisp_goods_source gs ON pi.INFO_ID = gs.GOODS_SOURCE_ID
AND pi.INFO_TYPE = #{infoType} AND pi.USER_ID = #{userId}
and gs.FROM_P_CODE = #{fromPCode}
and gs.FROM_C_CODE = #{fromCCode}
and gs.FROM_T_CODE = #{fromTCode}
and gs.TO_P_CODE = #{toPCode}
and gs.TO_C_CODE = #{toCCode}
and gs.TO_T_CODE = #{toTCode}
) t WHERE t.TOTAL_NUM > 0 ORDER BY t.PUBLISH_TIME DESC

mapper.update:
UPDATE pisp_waybill pwb
pwb.STATUS=#{status}, pwb.ORDER_STATUS=#{orderStatus}, pwb.PLACE_ORDER_TIME = #{placeOrderTime}, pwb.PROTOCOL_PHOTO = #{protocolPhoto}, pwb.INSURANCE_PHOTO = #{insurancePhoto}, pwb.UPDATE_USER = #{updateUser}, pwb.UPDATE_TIME = #{updateTime}, pwb.IS_E_EVALUATED = #{isEEvaluated}, pwb.IS_P_EVALUATED = #{isPEvaluated}, pwb.TRANSACTION_TIME = #{transactionTime}, pwb.IS_FINAL_PAY = #{isFinalPay}, pwb.ADVANCE_PRICE = #{advancePrice}, pwb.MID_PRICE = #{midPrice}, pwb.FINAL_PRICE = #{finalPrice},
WHERE pwb.WAYBILL_NO = #{waybillNo}

update.if:
update pisp_waybill pwb
pwb.INFO_CONTENT=#{infoContent},
pwb.PROTOCOL_PHOTO=#{protocolPhoto}, pwb.INSURANCE_PHOTO=#{insurancePhoto}, pwb.STATUS=#{status}, pwb.INSURANCE_PHOTO=#{insurancePhoto}, pwb.ORDER_STATUS=#{orderStatus}, pwb.TRANSACTION_TIME = #{transactionTime}, pwb.UPDATE_USER = #{updateUser}, pwb.UPDATE_TIME = #{updateTime}, pwb.ORDER_STATUS = #{orderStatus}
AND pwb.INFO_ID = #{infoId} AND pwb.INFO_TYPE =#{infoType}
AND pwb.WAYBILL_NO = #{waybillNo}

mapper.select:
SELECT pwb.INFO_CONTENT, pwb.WAYBILL_ID, pwb.WAYBILL_NO, pwb.HAULIER, pwb.HAULIER_NAME, pwb.HAULIER_PHONE, pwb.PROTOCOL_PHOTO, l.LOCATION_NAME, l.LATITUDE, l.LONGITUDE, l.LOCATE_TIME, gs.GOODS_CLASS, gs.FROM_PLACE, gs.TO_PLACE, gs.WEIGHT, weightdict.VAL_NAME AS WEIGHT_UNIT, gs.VOLUME, volumedict.VAL_NAME AS VOLUME_UNIT, gs.CAR_LENGTH_NEED, gs.CAR_LENGTH_NEED_NAME AS CAR_LENGTH_NEED_NAME, gs.CAR_TYPE_NEED_NAME AS CAR_TYPE_NEED_NAME, gs.PRICE, pricedict.VAL_NAME AS PRICE_UNIT, gs.LINKMAN, gs.LINK_MOBILE1, gs.LINK_MOBILE2, gs.LINK_PHONE, gs.LOAD_DATE, gs.GOODS_NAME, gs.PLACE_KILOMETRE, dictType.VAL_NAME AS GOODS_TYPE, gs.MARK, pub.PUBLISH_TIME, pwb.CREATE_TIME, pwb.SEND_GOODS_DATE, e.ENTERPRISE_NAME AS ENTERPRISE_NAME, pub.IS_STRUCT, e.ADDRESS FROM pisp_waybill pwb LEFT JOIN pisp_publish_info pub ON pub.INFO_ID = pwb.INFO_ID AND pub.INFO_TYPE = pwb.INFO_TYPE LEFT JOIN pisp_goods_source gs ON gs.GOODS_SOURCE_ID = pwb.INFO_ID AND pwb.INFO_TYPE = pub.INFO_TYPE LEFT JOIN pisp_driver_location l ON l.DRIVER = pwb.HAULIER LEFT JOIN pisp_user u ON u.USER_ID = pub.USER_ID LEFT JOIN pisp_euser e ON e.USER_ID = u.USER_ID LEFT JOIN pisp_dict_val weightdict ON weightdict.dict_code = 'GOODS_WEIGHT_UNIT' AND weightdict.val_code = gs.WEIGHT_UNIT LEFT JOIN pisp_dict_val volumedict ON volumedict.dict_code = 'GOODS_WEIGHT_UNIT' AND volumedict.val_code = gs.VOLUME_UNIT LEFT JOIN pisp_dict_val pricedict ON pricedict.dict_code = 'TRANSPORT_PRICE_UNIT' AND pricedict.val_code = gs.PRICE_UNIT LEFT JOIN pisp_dict_val dictType ON dictType.dict_code = 'GOODS_TYPE' AND dictType.val_code = gs.goods_type
AND pwb.STATUS = #{status}
AND pwb.HAULIER = #{haulier} AND pwb.SHIPPER = #{shipper} AND pwb.PROCESS_TYPE = #{processType}
ORDER BY pwb.TRANSACTION_TIME DESC

SELECT pwb.INFO_CONTENT, pwb.WAYBILL_ID, pwb.WAYBILL_NO, pwb.HAULIER, pwb.HAULIER_NAME, pwb.HAULIER_PHONE, pwb.CREATE_TIME, pwb.PROTOCOL_PHOTO, l.LOCATION_NAME, l.LATITUDE, l.LONGITUDE, l.LOCATE_TIME, gs.GOODS_CLASS, gs.FROM_PLACE, gs.TO_PLACE, gs.GOODS_NAME, gs.PLACE_KILOMETRE FROM pisp_waybill pwb LEFT JOIN pisp_publish_info pub ON pub.INFO_ID = pwb.INFO_ID AND pub.INFO_TYPE = pwb.INFO_TYPE LEFT JOIN pisp_goods_source gs ON gs.GOODS_SOURCE_ID = pwb.INFO_ID AND pwb.INFO_TYPE = pub.INFO_TYPE LEFT JOIN pisp_driver_location l ON l.DRIVER = pwb.HAULIER
AND pwb.STATUS = #{status}
AND pwb.SHIPPER = #{shipper} AND pwb.PROCESS_TYPE = #{processType}
ORDER BY pwb.TRANSACTION_TIME DESC

SELECT pwb.INFO_CONTENT, pwb.WAYBILL_ID, pwb.WAYBILL_NO, pwb.HAULIER, pwb.HAULIER_NAME, pwb.HAULIER_PHONE, pwb.FROM_PLACE, pwb.TO_PLACE, l.LOCATION_NAME, l.LATITUDE, l.LONGITUDE, l.LOCATE_TIME, gs.GOODS_CLASS, pwb.CREATE_TIME, pwb.SHIPPER_NAME, pwb.SHIPPER_PHONE, pub.PUBLISH_ID, pwb.PROTOCOL_PHOTO, gs.PLACE_KILOMETRE FROM pisp_waybill pwb LEFT JOIN pisp_driver_location l ON l.DRIVER = pwb.HAULIER LEFT JOIN pisp_publish_info pub ON pub.INFO_ID = pwb.INFO_ID AND pub.INFO_TYPE = pwb.INFO_TYPE LEFT JOIN pisp_goods_source gs ON gs.GOODS_SOURCE_ID = pwb.INFO_ID AND pwb.INFO_TYPE = 1
AND pwb.STATUS = #{status}
AND pwb.HAULIER = #{haulier} AND pwb.SHIPPER = #{shipper} AND pwb.PROCESS_TYPE = #{processType}
and gs.FROM_P_CODE = #{fromPCode}
and gs.FROM_C_CODE = #{fromCCode}
and gs.FROM_T_CODE = #{fromTCode}
and gs.TO_P_CODE = #{toPCode}
and gs.TO_C_CODE = #{toCCode}
and gs.TO_T_CODE = #{toTCode}
ORDER BY pwb.TRANSACTION_TIME DESC

SELECT w.WAYBILL_ID, w.WAYBILL_NO, w.HAULIER, w.HAULIER_NAME, w.HAULIER_PHONE, w.CREATE_TIME, w.`STATUS`, w.INFO_CONTENT, w.SHIPPER_NAME, w.SHIPPER_PHONE, pub.PUBLISH_ID, pgs.PLACE_KILOMETRE FROM pisp_waybill w LEFT JOIN pisp_publish_info pub ON pub.INFO_ID = w.INFO_ID AND pub.INFO_TYPE = w.INFO_TYPE LEFT JOIN pisp_goods_source pgs on pgs.GOODS_SOURCE_ID= pub.INFO_ID WHERE w.STATUS != 99 AND w.PROCESS_TYPE = 1
and w.USER_ID =#{userId}
and w.INFO_ID = #{infoId} and w.INFO_TYPE = #{infoType}
and w.FROM_P_CODE = #{fromPCode}
and w.FROM_C_CODE = #{fromCCode}
and w.FROM_T_CODE = #{fromTCode}
and w.TO_P_CODE = #{toPCode}
and w.TO_C_CODE = #{toCCode}
and w.TO_T_CODE = #{toTCode}
ORDER BY w.CREATE_TIME DESC

SELECT pw.USER_ID, pw.INFO_ID, pw.FROM_PLACE, pw.TO_PLACE, pw.INFO_CONTENT, pw.SHIPPER_NAME, pw.SHIPPER, pw.HAULIER, pw.ORDER_STATUS, pw.IS_CANCEL, gs.GOODS_CLASS, pw.WAYBILL_NO, pw.CREATE_TIME, gs.LINK_MOBILE1, gs.LINK_MOBILE2, gs.LINKMAN, pe.ENTERPRISE_NAME, pw.PROTOCOL_PHOTO, pw.INSURANCE_PHOTO, pw.STATUS FROM pisp_waybill pw LEFT JOIN pisp_goods_source gs ON pw.INFO_ID = gs.GOODS_SOURCE_ID LEFT JOIN pisp_euser pe ON pe.USER_ID = pw.SHIPPER
AND pw.STATUS = #{status} AND pw.ORDER_STATUS = #{orderStatus} AND pw.INFO_TYPE = #{infoType} AND pw.USER_ID = #{userId}
and gs.FROM_P_CODE = #{fromPCode}
and gs.FROM_C_CODE = #{fromCCode}
and gs.FROM_T_CODE = #{fromTCode}
and gs.TO_P_CODE = #{toPCode}
and gs.TO_C_CODE = #{toCCode}
and gs.TO_T_CODE = #{toTCode}
and (pw.IS_P_DELETE !=1 OR pw.IS_P_DELETE IS NULL )
and (pw.ORDER_STATUS = 21 OR pw.ORDER_STATUS = 22 OR pw.ORDER_STATUS = 23)
and (pw.ORDER_STATUS = 41 OR pw.ORDER_STATUS = 42 OR pw.ORDER_STATUS = 31)
and pw.IS_CANCEL is NULL
ORDER BY pw.CREATE_TIME DESC

SELECT COUNT(0)
FROM pisp_waybill pw
WHERE pw.USER_ID = #{userId}
AND pw.INFO_ID = #{infoId}

SELECT DISTINCT * FROM ( SELECT pi.PUBLISH_ID, pi.INFO_ID, gs.FROM_PLACE, gs.TO_PLACE, gs.INFO_CONTENT, gs.GOODS_CLASS, pi.PUBLISH_TIME, pw.CREATE_TIME, ( SELECT COUNT(w.WAYBILL_NO) FROM pisp_waybill w WHERE w.INFO_ID = pi.INFO_ID AND w.INFO_TYPE = pi.INFO_TYPE AND w. STATUS != 99 AND w. STATUS != -1 AND w.PROCESS_TYPE = 1 ) AS TOTAL_NUM, ( SELECT COUNT(w.WAYBILL_NO) FROM pisp_waybill w WHERE w.INFO_ID = pi.INFO_ID AND w.INFO_TYPE = pi.INFO_TYPE AND w.STATUS = 11 AND (w.ORDER_STATUS =11 OR w.ORDER_STATUS =-1 OR w.ORDER_STATUS =-2 OR w.ORDER_STATUS =-41 OR w.ORDER_STATUS =-51 OR w.ORDER_STATUS IS NULL) AND w.PROCESS_TYPE = 1 ) AS UN_HANDLE_NUM FROM pisp_publish_info pi LEFT JOIN pisp_goods_source gs ON pi.INFO_ID = gs.GOODS_SOURCE_ID INNER JOIN pisp_waybill pw ON pi.INFO_ID = pw.INFO_ID
AND pi.INFO_TYPE = #{infoType} AND pi.USER_ID = #{userId}
) t WHERE t.TOTAL_NUM > 0 AND t.UN_HANDLE_NUM > 0 GROUP BY t.PUBLISH_ID ORDER BY t.CREATE_TIME DESC

mapper.update:
UPDATE pisp_waybill pw 
SET 
	pw.ORDER_STATUS = #{orderStatus}
WHERE pw.WAYBILL_NO = #{waybillNo}

UPDATE pisp_waybill pw
pw.UPDATE_USER = #{updateUser}, pw.UPDATE_TIME = #{updateTime}, pw.TRANSACTION_TIME = #{transactionTime}, pw.RECEIVE_TIME = #{receiveTime}, pw.STATUS = #{status}, pw.ORDER_STATUS = #{orderStatus}, pw.START_TIME = #{startTime}, pw.IS_FINAL_PAY = #{isFinalPay}
WHERE pw.WAYBILL_NO = #{waybillNo}

update.if:
update pisp_waybill pwb
pwb.INFO_CONTENT=#{infoContent},
pwb.PROTOCOL_PHOTO=#{protocolPhoto}, pwb.INSURANCE_PHOTO=#{insurancePhoto}, pwb.STATUS=#{status}, pwb.INSURANCE_PHOTO=#{insurancePhoto}, pwb.ORDER_STATUS=#{orderStatus}, pwb.TRANSACTION_TIME = #{transactionTime}, pwb.UPDATE_USER = #{updateUser}, pwb.UPDATE_TIME = #{updateTime}, pwb.ORDER_STATUS = #{orderStatus}
AND pwb.INFO_ID = #{infoId} AND pwb.INFO_TYPE =#{infoType}
AND pwb.WAYBILL_NO = #{waybillNo}

mapper.select:
SELECT pw.WAYBILL_NO, pw.FROM_PLACE, pw.TO_PLACE, pe.ENTERPRISE_NAME,
pw.ADDRESS, pw.HAULIER_PHONE, pw.INSURANCE_PHOTO, pw.PROTOCOL_PHOTO, pw.HAULIER_NAME, pw.PRINCIPAL, pw.PRINCIPAL_PHONE, pw.TRANSATION_PRICE, pu.WAYBILL_MONEY, pw.ADVANCE_PRICE, pw.MID_PRICE, pw.FINAL_PRICE, pw.HAULIER, pw.ORDER_STATUS, pw.IS_FINAL_PAY FROM pisp_waybill pw
LEFT JOIN pisp_user pu ON pu.USER_ID = pw.SHIPPER LEFT JOIN pisp_euser pe ON pe.USER_ID = pw.SHIPPER LEFT JOIN pisp_puser pp ON pp.USER_ID = pu.USER_ID where pw.WAYBILL_NO=#{waybillNo}

mapper.update:
update pisp_waybill pwb
pwb.ORDER_STATUS = #{orderStatus}, pwb.STATUS = #{orderStatus}, pwb.UPDATE_TIME = #{updateTime}, pwb.TRANSACTION_TIME = #{transactionTimeStart}, pwb.IS_FINAL_PAY = #{isFinalPay},
AND pwb.WAYBILL_NO = #{waybillNo}

select.if:
SELECT pu.USER_ID, pu.WAYBILL_MONEY FROM pisp_pay_record ppr INNER JOIN pisp_user pu ON ppr.FROM_USER_ID = pu.USER_ID WHERE
ppr.OUT_TRADE_NO = #{outTradeNo}
pu.USER_ID = #{userId}

mapper.update:
